mapper.select:
		select * from app_info
		

			

				and softwareName like CONCAT("%",#{AppName},"%")
			
			
				and flatformId = #{Type}
			
			
				and devId = #{PcNameID}
			

			
				and categoryLevel1 = #{classify1}
			

			and categoryLevel2 = #{classify2}

			and categoryLevel3 = #{classify3}
